div.script:
$$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="demo"]').observe('click', function() {
    $$('[name="receptor_format"] option')[0].selected=true;
    new Ajax.Request('/portal/demos/MTiOpenScreen/4AG8_protein.pdb', {
        method:'get',
        onSuccess: function(r){
            $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="receptor_file.mode"][value="paste"]').fire('databox:switch');
            $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="receptor_file"]').value=r.responseText.strip()
        },
    });
    $$('[name="library"] option')[2].selected=true;
    $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="limit"]').value='100';
    $$('[name="is_lead_like"] option')[0].selected=true;
    $$('[name="grid_calculation_mode"] option')[1].selected=true;
    $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="gridcenter_x"]').value='20.192';
    $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="gridcenter_y"]').value='25.726';
    $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="gridcenter_z"]').value='35.76';
    $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="gridpts_x"]').value='19';
    $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="gridpts_y"]').value='13';
    $$('div[id^=\'ms-forms::\'].tabPanel').findAll(function(e){return e.visible()}).first().down('[name="gridpts_z"]').value='18';
});

